mapper.select:
select
`id`,`train_no`,`start_station_id`,`start_station_name`,`end_station_id`,`end_station_name`,`consignor`,`consignee`,`product_name`,`train_type`,`project_no`,`loading_line`,`description`,`person_ids`,`person_names`,`continue_shot`,`order_start_time`,`order_end_time`,`order_state`,`del_state`,`create_time`,`create_user`,`modify_time`,`modify_user`
from tbl_order



            upload_way = #{uploadWay}


    and id = #{id}
    and train_no = #{trainNo}
    and start_station_id = #{startStationId}
    and start_station_name =
        #{startStationName}

    and end_station_id = #{endStationId}
    and end_station_name = #{endStationName}
    and consignor = #{consignor}
    and consignee = #{consignee}
    and product_name = #{productName}
    and train_type = #{trainType}
    and project_no = #{projectNo}
    and loading_line = #{loadingLine}
    and description = #{description}
    and person_ids = #{personIds}
    and person_names = #{personNames}
    and continue_shot = #{continueShot}
    and order_start_time = #{orderStartTime}
    and order_end_time = #{orderEndTime}
    and order_state = #{orderState}
    and del_state = #{delState}
    and create_time = #{createTime}
    and create_user = #{createUser}
    and modify_time = #{modifyTime}
    and modify_user = #{modifyUser}

    and create_time >= #{beginTime}
    and create_time <= #{endTime}


            order by ${sort} ${order}


            order by create_time desc




    limit #{offset}, #{limit}

select
`id`,`train_no`,`start_station_id`,`start_station_name`,`end_station_id`,`end_station_name`,`consignor`,`consignee`,`product_name`,`train_type`,`project_no`,`loading_line`,`description`,`person_ids`,`person_names`,`continue_shot`,`order_start_time`,`order_end_time`,`order_state`,`del_state`,`create_time`,`create_user`,`modify_time`,`modify_user`
from tbl_order



            upload_way = #{uploadWay}


    and start_station_id = #{startStationId}

GROUP BY substr(create_time,1,10) order by create_time desc

    limit #{offset}, #{limit}

select count(*) from tbl_order where upload_way = 0
