  		   and del_state = #{delState}

mapper.update:
update tbl_order
train_no = #{trainNo}, start_station_id = #{startStationId}, start_station_name = #{startStationName}, end_station_id = #{endStationId}, end_station_name = #{endStationName}, consignor = #{consignor}, consignee = #{consignee}, product_name = #{productName}, train_type = #{trainType}, project_no = #{projectNo}, loading_line = #{loadingLine}, description = #{description}, person_ids = #{personIds}, person_names = #{personNames}, continue_shot = #{continueShot}, start_time = #{startTime}, end_time = #{endTime}, order_state = #{orderState}, del_state = #{delState}
where id = #{id}
