mapper.select:
select id,login_name as loginName,name,phone,station_id as stationId,station_name as stationName from tbl_person

	 and id = #{id} 
	 and login_name = #{loginName} 
	 and password = #{password}

		select id,login_name,password,name,phone,station_id,station_name,del_state,create_time,create_user,modify_time,modify_user from tbl_person

		  		   and id = #{id} 
		  		   and login_name = #{loginName} 
		  		   and password = #{password} 
		  		   and name = #{name} 
		  		   and phone = #{phone} 
		  		   and station_id = #{stationId} 
		  		   and station_name = #{stationName} 
		  		   and del_state = #{delState} 
		  		   and create_time = #{createTime} 
		  		   and create_user = #{createUser} 
		  		   and modify_time = #{modifyTime} 
		  		   and modify_user = #{modifyUser} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from tbl_person

  		   and id = #{id} 
  		   and login_name = #{loginName} 
  		   and password = #{password} 
