mapper.update:
update tbl_person
login_name = #{loginName}, password = #{password}, name = #{name}, phone = #{phone}, station_id = #{stationId}, station_name = #{stationName}, del_state = #{delState}, create_time = #{createTime}, create_user = #{createUser}, modify_time = #{modifyTime}, modify_user = #{modifyUser}
