mapper.select:
		SELECT `name`, phone, station_id, station_name, account, del_state, create_time, create_user, modify_time, modify_user FROM tbl_person

		  		   and id = #{id} 
		  		   and name = #{name} 
		  		   and phone = #{phone} 
		  		   and station_id = #{stationId} 
		  		   and station_name = #{stationName} 
		  		   and account = #{account} 
		  		   and del_state = #{delState} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from tbl_person

