		  		   and parent_id = #{parentId} 
		  		   and del_state = #{delState} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from `tbl_station`

  		   and id = #{id} 
  		   and name = #{name} 
  		   and parent_id = #{parentId} 
  		   and del_state = #{delState}

